svg.script:
function switchOpacity(svgObject) {
	var opacity = svgObject.getAttribute('opacity');
	if (opacity < 1.0) {
		opacity = 1.0;
	} else {
		opacity = 0.2;
	}
	svgObject.setAttribute('opacity',opacity);
}
function click(evt) {
	switchOpacity(evt.target);
	return false; // same as evt.preventDefault(); evt.stopPropagation();
}
//
